package mok.it.tortura.feature.evaluation

import androidx.compose.runtime.mutableStateOf
import androidx.lifecycle.ViewModel
import mok.it.tortura.CompetitionDb


const val EVALUATION_HTML_STATIC_END = "  </table>\n" +
        "\n" +
        "  <!-- Placeholder for bottom logo -->\n" +
        "<svg xmlns=\"http://www.w3.org/2000/svg\" class=\"bottom-logo\" xmlns:xlink=\"http://www.w3.org/1999/xlink\"  zoomAndPan=\"magnify\" viewBox=\"0 0 375 374.999991\"  preserveAspectRatio=\"xMidYMid meet\" version=\"1.2\"><defs><clipPath id=\"9a316d1f76\"><path d=\"M 0 68 L 375 68 L 375 292.714844 L 0 292.714844 Z M 0 68 \"/></clipPath></defs><g id=\"781d2f159b\"><g clip-rule=\"nonzero\" clip-path=\"url(#9a316d1f76)\"><g transform=\"matrix(0.754527,0,0,0.755033,0.0000045,67.714169)\"><image width=\"497\" xlink:href=\"data:image/jpeg;base64,\" preserveAspectRatio=\"xMidYMid meet\"/></g></g></g></svg> " +
        "\n" +
        "</body>\n" +
        "</html>"

class EvaluationViewModel : ViewModel() {
    val competitions = mutableStateOf(CompetitionDb.getCompetitions())
    val tabIndex = mutableStateOf(0)

    fun getCsv(): String {
        val competition = competitions.value[tabIndex.value]
        val answersInPointOrder = competition.answers.sortedBy { it.answer.points() }.reversed()
        var previousPlace = 0
        var previousPoints = -1.0
        var csv = "helyezés,csapatID,pontszám,csapattag1,csapattag2,csapattag3,csapattag4\n"

        answersInPointOrder.forEach { answer ->
            val place =
                if (answer.answer.points() == previousPoints) previousPlace else answersInPointOrder.indexOf(answer) + 1
            previousPlace = place
            previousPoints = answer.answer.points()
            csv += "$place," +
                    "${competition.teamAssignment.baseTeamId + competition.teamAssignment.teams.indexOf(answer.team)}," +
                    "${answer.answer.points()}," +
                    answer.team.students.sortedBy { it.name }.joinToString(separator = ",") { it.name } +
                    "\n"
        }
        return csv
    }

    fun getPrintableHtml(): String {
        val competition = competitions.value[tabIndex.value]
        val answersInPointOrder = competition.answers.sortedBy { it.answer.points() }.reversed()
        var html = "<!DOCTYPE html>\n" +
                "<html lang=\"hu\">\n" +
                "<head>\n" +
                "  <meta charset=\"UTF-8\">\n" +
                "  <title>Tortúra eredmények - ${competition.category}</title>\n" +
                "  <style>\n" +
                "    body {\n" +
                "      font-family: 'Poppins';\n" +
                "      text-align: center;\n" +
                "      margin: 15px;\n" +
                "    }\n" +
                "    .top-left-logo {\n" +
                "      position: absolute;\n" +
                "      top: 20px;\n" +
                "      left: 20px;\n" +
                "      width: 60px;\n" +
                "    }\n" +
                "    h1 {\n" +
                "      font-size: 36px;\n" +
                "      margin-bottom: 10px;\n" +
                "    }\n" +
                "    h2 {\n" +
                "      font-size: 28px;\n" +
                "      margin-top: 0;\n" +
                "      margin-bottom: 40px;\n" +
                "    }\n" +
                "    table {\n" +
                "      margin: 0 auto 10px auto;\n" +
                "      border-collapse: collapse;\n" +
                "      font-size: 14px;\n" +
                "    }\n" +
                "    th, td {\n" +
                "      border: 1px solid black;\n" +
                "      padding: 5px 5px;\n" +
                "    }\n" +
                "    .bottom-logo {\n" +
                "      width: 200px;\n" +
                "      margin-top: 20px;\n" +
                "    }\n" +
                "  </style>\n" +
                "</head>\n" +
                "<body>\n" +
                "\n" +
                //"  <!-- Placeholder for top left logo -->\n" +
                "<svg xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\" class=\"top-left-logo\" width=\"500\" zoomAndPan=\"magnify\" viewBox=\"0 0 375 374.999991\" preserveAspectRatio=\"xMidYMid meet\" version=\"1.2\"><defs><clipPath id=\"7b7efe6cf9\"><path d=\"M 32.914062 26.449219 L 357.664062 26.449219 L 357.664062 341.449219 L 32.914062 341.449219 Z M 32.914062 26.449219 \"/></clipPath></defs><g id=\"5d77c5ee65\"><g clip-rule=\"nonzero\" clip-path=\"url(#7b7efe6cf9)\"><g mask=\"url(#9f1b78e3fc)\" transform=\"matrix(0.381668,0,0,0.381668,-5.744955,-44.278413)\"><image width=\"2362\" xlink:href=\"data:image/png;base64,\" height=\"1181\" preserveAspectRatio=\"xMidYMid meet\"/><mask id=\"9f1b78e3fc\"><g><image width=\"2362\" xlink:href=\"data:image/png;base64,\" height=\"1181\" preserveAspectRatio=\"xMidYMid meet\"/></g></mask></g></g></g></svg> " +
                "\n" +
                "  <!-- Title and subtitle -->\n" +
                "  <h1>Tortúra eredmények</h1>\n" +
                "  <h2>${competition.category}</h2>\n" +
                "\n" +
                "  <!-- Results table -->\n" +
                "  <table>\n" +
                "    <tr>\n" +
                "      <th>Hely</th>\n" +
                "      <th>Sorszám</th>\n"

        var previousPlace = 0
        var previousPoints = -1.0

        html += "      <th>Csapattagok</th>\n" +
                "      <th>Pont</th>\n" +
                "    </tr>\n"

        answersInPointOrder.forEach { answer ->
            val place =
                if (answer.answer.points() == previousPoints) previousPlace else answersInPointOrder.indexOf(answer) + 1
            previousPlace = place
            previousPoints = answer.answer.points()
            html += "    <tr>\n" +
                    "      <td>$place.</td>\n" +
                    "      <td>${competition.teamAssignment.baseTeamId + competition.teamAssignment.teams.indexOf(answer.team)}</td>\n" +
                    "      <td>${
                        answer.team.students.sortedBy { it.name }.joinToString(separator = ", ") { it.name }
                    }</td>\n" +
                    "      <td>${answer.answer.points().toString().removeSuffix(".0")}</td>\n" +
                    "    </tr>\n"
        }
        html += EVALUATION_HTML_STATIC_END
        return html
    }

    fun onEvent(event: EvaluationEvent) {
        when (event) {
            is EvaluationEvent.SelectTabIndex ->
                tabIndex.value = event.index
        }
    }
}

sealed class EvaluationEvent() {
    class SelectTabIndex(val index: Int) : EvaluationEvent()
}